mapper.update:
UPDATE area
SET coastline_length = #{coastlineLength}
WHERE id=#{id}

mapper.select:
SELECT FROM area WHERE delete_mark = 0
AND area_name LIKE concat("%",#{areaName},"%")
AND img_time > #{timeBegin}
AND img_time < #{timeEnd}
ORDER BY id DESC

SELECT 
FROM area
WHERE delete_mark = 0 AND img_name=#{imgName}
